import './Preview.css';

function Preview({product: {productType, productName, productDesc, price, priceReduced}}) {
    return (
        <main>
            <div>
                <img src='./images/image-product-desktop.jpg'/>
            </div>
            <div>
                <p>{productType}</p>

                <h1>{productName}</h1>

                <p>{productDesc}</p>
                <p>{price}</p>
                <p>{priceReduced}</p>

                <p>Add to Cart</p>
            </div>
        </main>
    );
}

export default Preview;
